//CALCULAR PORCENTAJES BENEFICIARIOS
const inputs = [
document.getElementById('beneficiary_percentage1'),
document.getElementById('beneficiary_percentage2'),
document.getElementById('beneficiary_percentage3'),
document.getElementById('beneficiary_percentage4')
];
inputs[0].innerText ='90';
const sections = [
document.querySelector("[data-name='beneficiary2']"),
document.querySelector("[data-name='beneficiary3']"),
document.querySelector("[data-name='beneficiary4']")
];

const buttons = [
document.getElementById("add_beneficiary2"),
document.getElementById("add_beneficiary3"),
document.getElementById("add_beneficiary4")
];
sections.forEach(section => section.style.display = 'none');

buttons.forEach((button, index) => {
button.addEventListener("click", function() {
console.log("clic en mas...");
toggleSection(index);
});
});

function toggleSection(index) {
const section = sections[index];
const input = inputs[index + 1];
if (section.style.display === 'none') {
section.style.display = 'block';
buttons[index].innerText = "-";
input.innerHTML = '';
} else {
section.style.display = 'none';
buttons[index].innerText = "+";
input.innerHTML = '';
}
validateInputs();
}

function validateInputs() {
let total = 0;
let visibleInputs = 0;

inputs.forEach(input => {
if (input.parentElement.style.display !== 'none') {
let value = parseInt(input.value) || 0;
total += value;
visibleInputs++;
}
});

if (visibleInputs === 1) {
inputs[0].innerHTML = 100;
inputs[0].disabled = true;
} else {
inputs[0].disabled = false;
}

if (total > 100) {
alert('La suma total no puede exceder 100');
let exceededAmount = total - 100;
for (let i = visibleInputs - 1; i >= 0; i--) {
let value = parseInt(inputs[i].value) || 0;
if (value >= exceededAmount) {
inputs[i].innerText = value - exceededAmount;
break;
}
}
}

}

});
