form.script:
document.addEventListener("DOMContentLoaded", function() {
var aboutVacancySelect = document.querySelector("select[name='about_vacancy']");
var otherReasonSection = document.querySelector("[data-name='other_vacancy']");
var otherReasonInput = document.getElementById("other_reason");
otherReasonSection.style.display = 'none';
aboutVacancySelect.addEventListener("change", function() {
if (this.value === 'other') {
otherReasonSection.style.display = 'block';
otherReasonInput.setAttribute('required', 'true');
} else {
otherReasonSection.style.display = 'none';
otherReasonInput.removeAttribute('required');
}
});
document.getElementById("curp").addEventListener("input", function() {
this.value = this.value.toUpperCase();
});
});

div.script:
document.addEventListener("DOMContentLoaded", function() {
const multipleFiles = ["rfc_files", "nss_files","driver_files"];
multipleFiles.forEach(id => {
const inputElement = document.getElementById(id);
if (inputElement) {
inputElement.setAttribute('multiple', 'multiple');
}
});

// Lista de IDs de los elementos de entrada que deben ser numéricos
const numericInputIds = ["exterior_number",
"interior_number","recruitment3"];
numericInputIds.forEach(id => {
const inputElement = document.getElementById(id);
if (inputElement) {
inputElement.addEventListener("input", function(event) {
event.target.value = event.target.value.replace(/\D/g, '');
if(id="recruitment3"){
console.log("telefono");
if (event.target.value > 10) {
event.target.value = event.target.value.slice(0, 10);
}
}
});
}
});
//GET AGE AUTOMATICALLY
document.getElementById("birthdate").addEventListener("change", function() {
var birthdate = new Date(this.value);
var today = new Date();
var age = today.getFullYear() - birthdate.getFullYear();
var m = today.getMonth() - birthdate.getMonth();
if (m < 0 || (m === 0 && today.getDate()<
birthdate.getDate())) {
age--;
}
document.getElementById("age").value = age;
var ineTutorSec = document.querySelector("[data-name='ineTutor_document']");
var complementInput = document.getElementById("ineTutor_document");
var complementLabel = document.getElementById("complementLabel");
var complementRequired = document.getElementById("complementRequired");
if (age < 18 ) {
complementLabel.innerText="INE Tutor";
complementRequired.innerText="*";
complementInput.setAttribute('required', 'true');
} else {
complementLabel.innerText="Complement";
complementRequired.innerText="";
complementInput.removeAttribute('required');
complementInput.setAttribute('multiple', 'multiple');
}
});
//Get gender
var genderSelect = document.getElementById("gender");
var curpInput = document.getElementById("curp");
document.getElementById("curp").addEventListener("change", function() {
var curp = this.value.toUpperCase();
//var genderChar = curp.charAt(10);

if (curp.length > 10) {
var genderChar = curp.charAt(10);

if (genderChar === 'H') {
genderSelect.value = 'male';
} else if (genderChar === 'M') {
genderSelect.value = 'female';
} else {
genderSelect.value = 'other';
}
// genderSelect.disabled = true;
} else {
genderSelect.value = 'other';
}
});
if (!curpInput.value || curpInput.value.length <= 1) {
genderSelect.value = 'other';
//genderSelect.disabled = true;
}
