//CURRENT STUDYING
var is_studyingSelect =
document.querySelector("select[name='is_studying']");
var current_schedule =
document.querySelector("[data-name='current_schedule']");
var current_degree = document.querySelector("[data-name='current_degree']");

var current_scheduleReq = document.getElementById("current_schedule");
var current_degreeReq = document.getElementById("current_degree");

current_schedule.style.display = 'none';
current_degree.style.display = 'none';

is_studyingSelect.addEventListener("change", function() {
if (this.value === 'yes') {
current_schedule.style.display = 'block';
current_scheduleReq.setAttribute('required', 'true');
current_degree.style.display = 'block';
current_degreeReq.setAttribute('required', 'true');
} else {
current_schedule.style.display = 'none';
current_scheduleReq.removeAttribute('required');
current_degree.style.display = 'none';
current_degreeReq.removeAttribute('required');
}
});
