document.getElementById("rfc_number").addEventListener("input", function() {
this.value = this.value.toUpperCase();
// Limitar a 13 caracteres
if (this.value.length > 13) {
this.value = this.value.slice(0, 13);
}
});
//CP limitado a 5 caracteres
document.getElementById("postal_code").addEventListener("input", function()
{
this.value = this.value.toUpperCase();
if (this.value.length > 5) {
this.value = this.value.slice(0, 5);
}
});
//DRIVER
var driverSelect =
document.querySelector("select[name='has_driver_license']");
var driverSection =
document.querySelector("[data-name='driver_license_number']");
var driverInput = document.getElementById("driver_license_number");
var validitySection =
document.querySelector("[data-name='driver_license_validity']");
var driver_filesSection =
document.querySelector("[data-name='driver_files']");
var validityInput = document.getElementById("driver_license_validity");

driverSection.style.display = 'none';
validitySection.style.display = 'none';
driver_filesSection.style.display = 'none';
driverSelect.addEventListener("change", function() {
if (this.value === 'yes') {
driverSection.style.display = 'block';
driver_filesSection.style.display = 'block';
driverInput.setAttribute('required', 'true');
validitySection.style.display = 'block';
validityInput.setAttribute('required', 'true');
} else {
driverSection.style.display = 'none';
driver_filesSection.style.display = 'none';
driverInput.removeAttribute('required');
validitySection.style.display = 'none';
validityInput.removeAttribute('required');
}
});
