document.querySelector("[data-name='experience1']");
var experienceSection2 =
document.querySelector("[data-name='experience2']");
var experienceSection3 =
document.querySelector("[data-name='experience3']");

experienceSection1.style.display = 'none';
experienceSection2.style.display = 'none';
experienceSection3.style.display = 'none';

experience1_exp.style.display = 'none';
experience2_exp.style.display = 'none';
experience3_exp.style.display = 'none';
experienceSelect.addEventListener("change", function() {
if (this.value === 'yes') {
experience1_exp.style.display = 'block';
experience2_exp.style.display = 'block';
experience3_exp.style.display = 'block';
}else{
experience1_exp.style.display = 'none';
experience2_exp.style.display = 'none';
experience3_exp.style.display = 'none';}
});
experience1Select.addEventListener("click", function() {
if (experienceSection1.style.display === 'none') {
experienceSection1.style.display = 'block';
} else {
